export const required = value => {
    if (value) {
        return undefined;
    }
    
    return "Field is required";
}

export const maxLength30 = value => {
    if (!value) {
        return "Value not set"
    }
    
    if (value.length < 30) {
        return undefined;
    }
    
    return "Max length is 30 symbols";
}
