import React from "react";
import style from "./Users.module.css"
import userPhoto from "./../../assets/images/user-no-photo.png"
import { NavLink } from "react-router-dom";
import Paginator from "./Paginator/Paginator";

let Users = (props) => {
    return (
        <div className={style.usersContainer}>
            <div className={style.paginationButtonsContainer}>
                <Paginator totalUsersCount={props.totalUsersCount} pageSize={props.pageSize} currentPage={props.currentPage} onPageNumberChanged={props.onPageNumberChanged} />
            </div>
            {
                props.users.map(u => (
                    <div key={u.id} className={style.item}>
                        <NavLink to={"/profile/" + u.id}>
                            <div className={style.userPhoto}>
                                <img src={u.photos.small != null ?
                                    u.photos.small
                                    : userPhoto} alt="user avatar" />
                            </div>
                        </NavLink>
                        <div className={style.userDescription}>
                            <div>{u.name}</div>
                            <div>{u.status}</div>

                        </div>
                        <div className={style.userLocation}>
                            <div>{"u.location.country"}</div>
                            <div>{"u.location.city"}</div>
                        </div>
                        <div className={style.buttonContainer}>
                            {u.followed ?
                                <button disabled={props.followingInProgress.some(id => id === u.id)}
                                    className={style.button}
                                    onClick={() => { props.unfollow(u.id); }}
                                >Unfollow</button>
                                : <button disabled={props.followingInProgress.some(id => id === u.id)} className={style.button}
                                    onClick={() => { props.follow(u.id); }}
                                >Follow</button>
                            }
                        </div>
                    </div>
                ))
            }
        </div >
    );
}

export default Users;
